/*"f010003s": {
            "title": "FCM токен",
            "info": "Мобильное устройство получает токен FCM от сервера GOOGLE и отправляется на сервер, он необходим для отправки уведомлений"
        }, */
'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.idToken)) {
			resolve({ "result": false });
			return;
		}
		//console.log(obj, client);
		db.query(`UPDATE trade.token AS t SET t_fcm=$1, t_mtime = NOW()
			 WHERE t.t_name=$2;`, [obj.token, client.idToken]).then(
			(value) => {
				resolve({});
			});
	});
};