/*eslint no-unused-vars: ["error", { "args": "none" }]*/
/**"f020007g": {
            "title": "Типы групп номенклатуры",
            "info": "Мобильный клиент получает типы групп номенклатуры"
        }, */
'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.idToken)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT
					igt_id,
					igt_name,
					igt_priority,
					igt_agent,
					igt_active,
					extract(epoch from igt_mtime)::integer as igt_mtime
				from trade.item_Group_Types igt;`;
		} else {
			q = `SELECT
					igt_id,
					igt_name,
					igt_priority,
					igt_agent,
					igt_active,
					extract(epoch from igt_mtime)::integer as igt_mtime
				from trade.item_Group_Types igt
				JOIN (
					SELECT MAX(st2.st_stime) AS st_stime
					FROM trade.sync_token st2
					WHERE st2.t_id = $1 AND st2.st_table = 'f020007g' AND st2.st_result = TRUE
				) AS st ON (st.st_stime <= igt.igt_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020007g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					}
					);

			});
	});
};