/**"f020010g": {
            "title": "Единицы измерения",
            "info": "Мобильный клиент получает единицы измерения номенклатуры"
        }, */
'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.auth)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT
					iut_id,
					iut_name,
					imt_id,
					iut_okei,
					iut_active,
					extract(epoch from iut_mtime)::integer as iut_mtime
				from trade.item_Unit_Types`;
		} else {
			q = `SELECT
					iut_id,
					iut_name,
					imt_id,
					iut_okei,
					iut_active,
					extract(epoch from iut_mtime)::integer as iut_mtime
				from trade.item_Unit_Types iut
				JOIN (
					SELECT MAX(st2.st_stime) AS st_stime
					FROM trade.sync_token st2
					WHERE st2.t_id = $1 AND st2.st_table = 'f020010g' AND st2.st_result = TRUE
				) AS st ON (st.st_stime <= iut.iut_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020010g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					});
			});
	});
};