/**"f020011g": {
            "title": "Связь номенклатуры и единиц измерения",
            "info": "Мобильный клиент получает связи номенклатуры и единиц измерения"
        }, */
'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.auth)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT
					iu_id,
					i_id,
					iut_id,
					iu_ean,
					iu_krat,
					iu_num,
					iu_denum,
					iu_gros,
					iu_net,
					iu_length,
					iu_width,
					iu_height,
					iu_area,
					iu_volume,
					iu_agent,
					iu_base,
					iu_main,
					iu_active,
					extract(epoch from iu_mtime)::integer as iu_mtime
				from trade.item_Units`;
		} else {
			q = `SELECT
					iu_id,
					i_id,
					iut_id,
					iu_ean,
					iu_krat,
					iu_num,
					iu_denum,
					iu_gros,
					iu_net,
					iu_length,
					iu_width,
					iu_height,
					iu_area,
					iu_volume,
					iu_agent,
					iu_base,
					iu_main,
					iu_active,
					extract(epoch from iu_mtime)::integer as iu_mtime
				from trade.item_Units iu
				JOIN (
					SELECT MAX(st2.st_stime) AS st_stime
					FROM trade.sync_token st2
					WHERE st2.t_id = $1 AND st2.st_table = 'f020011g' AND st2.st_result = TRUE
				) AS st ON (st.st_stime <= iu.iu_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020011g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					});
			});
	});
};