/*eslint no-console: 0, "quotes": [0, "single"], module: 0, globals:[0, "obj"]*/
/*eslint no-unused-vars: ["error", { "args": "none" }]*/
/** "f020012g": {
            "title": "Строка поиска номенклатуры",
            "info": "Мобильный клиент получает строку поиска номенклатуры. Может содержать данные которых нет на мобильном клиенте"
        }, */
'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.idToken)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT i.i_id, concat_ws(' ', ig1.ig_value, ig2.ig_value, ig3.ig_value, i_name) as value FROM trade.items i
					LEFT JOIN trade.link_item_group lig1 ON (i.i_id = lig1.i_id) AND (lig1.igt_id=1)
					LEFT JOIN trade.item_groups ig1 ON lig1.ig_id = ig1.ig_id
					LEFT JOIN trade.link_item_group lig2 ON (i.i_id = lig2.i_id) AND (lig2.igt_id=2)
					LEFT JOIN trade.item_groups ig2 ON lig2.ig_id = ig2.ig_id
					LEFT JOIN trade.link_item_group lig3 ON (i.i_id = lig3.i_id) AND (lig3.igt_id=3)
					LEFT JOIN trade.item_groups ig3 ON lig3.ig_id = ig3.ig_id;`;
		} else {
			q = `SELECT i.i_id, concat_ws(' ', ig1.ig_value, ig2.ig_value, ig3.ig_value, i_name) as value FROM trade.items i
					LEFT JOIN trade.link_item_group lig1 ON (i.i_id = lig1.i_id) AND (lig1.igt_id=1)
					LEFT JOIN trade.item_groups ig1 ON lig1.ig_id = ig1.ig_id
					LEFT JOIN trade.link_item_group lig2 ON (i.i_id = lig2.i_id) AND (lig2.igt_id=2)
					LEFT JOIN trade.item_groups ig2 ON lig2.ig_id = ig2.ig_id
					LEFT JOIN trade.link_item_group lig3 ON (i.i_id = lig3.i_id) AND (lig3.igt_id=3)
					LEFT JOIN trade.item_groups ig3 ON lig3.ig_id = ig3.ig_id
					JOIN (
						SELECT MAX(st2.st_stime) AS st_stime
						FROM trade.sync_token st2
						WHERE st2.t_id = $1 AND st2.st_table = 'f020012g' AND st2.st_result = TRUE
					) AS st ON (st.st_stime <= i.i_mtime)
								OR (st.st_stime <= ig1.ig_mtime)
								OR (st.st_stime <= ig2.ig_mtime)
								OR (st.st_stime <= ig3.ig_mtime)
								OR (st.st_stime <= lig1.lig_mtime)
								OR (st.st_stime <= lig2.lig_mtime)
								OR (st.st_stime <= lig3.lig_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020012g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					});
			},(err)=>{console.error(err, 12);});
	});
};