/*eslint no-unused-vars: ["error", { "args": "none" }]*/
/**"f020013g": {
            "title": "Контрагенты",
            "info": "Мобильный клиент получает данные справочника контрагентов"
        }, */
'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.auth)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT
					ca_id,
					cat_id,
					ca_head,
					ca_name,
					ca_prn,
					ca_inn,
					ca_kpp,
					ca_client,
					ca_supplier,
					ca_carrier,
					ca_active,
					extract(epoch from ca_mtime)::integer as ca_mtime
					from trade.countragents;`;
		} else {
			q = `SELECT
					ca_id,
					cat_id,
					ca_head,
					ca_name,
					ca_prn,
					ca_inn,
					ca_kpp,
					ca_client,
					ca_supplier,
					ca_carrier,
					ca_active,
					extract(epoch from ca_mtime)::integer as ca_mtime
					from trade.countragents ca
					JOIN (
						SELECT MAX(st2.st_stime) AS st_stime
						FROM trade.sync_token st2
						WHERE st2.t_id = $1 AND st2.st_table = 'f020013g' AND st2.st_result = TRUE
					) AS st ON (st.st_stime <= ca.ca_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020013g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					});
			});
	});
};