/*eslint no-unused-vars: ["error", { "args": "none" }]*/
/**"f020014g": {
            "title": "Точки доставки",
            "info": "Мобильный клиент получает данные справочника Партнеры"
        }, */
'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.idToken)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT
					dp_id,
					dp_name,
					dp_prn,
					dp_client,
					dp_supplier,
					dp_carrier,
					dp_active,
					extract(epoch from dp_mtime)::integer as dp_mtime
				from trade.delivery_points`;
		} else {
			q = `SELECT
					dp_id,
					dp_name,
					dp_prn,
					dp_client,
					dp_supplier,
					dp_carrier,
					dp_active,
					extract(epoch from dp_mtime)::integer as dp_mtime
				from trade.delivery_points dp
				JOIN (
					SELECT MAX(st2.st_stime) AS st_stime
					FROM trade.sync_token st2
					WHERE st2.t_id = $1 AND st2.st_table = 'f020014g' AND st2.st_result = TRUE
				) AS st ON (st.st_stime <= dp.dp_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020014g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					});
			});
	});
};