/*"f020019g": {
			"title": "Организации",
			"info": "Получить список организаций",
			"example body": {},
			"example return": {}
		},*/
'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.auth)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT
				org_id,
				org_exid,
				org_name,
				org_short,
				org_full,
				org_inn,
				org_kpp,
				org_ogrn,
				org_okpo,
				org_active,
				extract(EPOCH FROM org_mtime) :: INTEGER AS org_mtime
			FROM trade.organization org`;
		} else {
			q = `SELECT
				org_id,
				org_exid,
				org_name,
				org_short,
				org_full,
				org_inn,
				org_kpp,
				org_ogrn,
				org_okpo,
				org_active,
				extract(EPOCH FROM org_mtime) :: INTEGER AS org_mtime
			FROM trade.organization org
			JOIN (
				SELECT MAX(st2.st_stime) AS st_stime
				FROM trade.sync_token st2
				WHERE st2.t_id = $1 AND st2.st_table = 'f020019g' AND st2.st_result = TRUE
			) AS st ON (st.st_stime <= org.org_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020019g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					});
			});
	});
};
