'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.auth)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT
                p_id,
                p_exid,
                p_name,
                p_f,
                p_i,
                p_o,
                p_sex,
                p_birthdate,
                p_active,
                extract(EPOCH FROM p_mtime) :: INTEGER AS p_mtime
            FROM trade.people p;`;
		} else {
			q = `SELECT
                p_id,
                p_exid,
                p_name,
                p_f,
                p_i,
                p_o,
                p_sex,
                p_birthdate,
                p_active,
                extract(EPOCH FROM p_mtime) :: INTEGER AS p_mtime
            FROM trade.people p
			JOIN (
				SELECT MAX(st2.st_stime) AS st_stime
				FROM trade.sync_token st2
				WHERE st2.t_id = $1 AND st2.st_table = 'f020021g' AND st2.st_result = TRUE
			) AS st ON (st.st_stime <= p.p_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020021g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					});
			});
	});
};
