'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.auth)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT
                kik_id, 
                kit_id, 
                kik_name, 
                kik_active, 
                extract(EPOCH FROM kik_mtime) :: INTEGER AS kik_mtime
            FROM trade.ki_kind kik;`;
		} else {
			q = `SELECT
                kik_id, 
                kit_id, 
                kik_name, 
                kik_active, 
                extract(EPOCH FROM kik_mtime) :: INTEGER AS kik_mtime
            FROM trade.ki_kind kik
			JOIN (
				SELECT MAX(st2.st_stime) AS st_stime
				FROM trade.sync_token st2
				WHERE st2.t_id = $1 AND st2.st_table = 'f020026g' AND st2.st_result = TRUE
			) AS st ON (st.st_stime <= kik.kik_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020026g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					});
			});
	});
};
