'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.idToken)) {
			resolve({ "result": false });
			return;
		}
		var q;
		if (obj.fullsync === true) {
			q = `SELECT
                kil_id, 
                at_id, 
                any_id, 
                kiv_id, 
                kil_active, 
                extract(EPOCH FROM kil_mtime) :: INTEGER AS kil_mtime
            FROM trade.ki_link kil;`;
		} else {
			q = `SELECT
                kil_id, 
                at_id, 
                any_id, 
                kiv_id, 
                kil_active, 
                extract(EPOCH FROM kil_mtime) :: INTEGER AS kil_mtime
            FROM trade.ki_link kil
			JOIN (
				SELECT MAX(st2.st_stime) AS st_stime
				FROM trade.sync_token st2
				WHERE st2.t_id = $1 AND st2.st_table = 'f020028g' AND st2.st_result = TRUE
			) AS st ON (st.st_stime <= kil.kil_mtime);`;
		}
		db.query(q, [client.t_id]).then(
			(value) => {
				db.one(`INSERT INTO trade.sync_token (t_id, st_table, st_stime, st_etime, st_result) VALUES
  						($1,'f020028g',now(),null, FALSE ) RETURNING trade.sync_token.st_id`, [client.t_id]).then(
					(st) => {
						var sync = +st.st_id;
						resolve({ "data": value, sync });
					});
			});
	});
};
