'use strict';
exports.fun = function (client, obj, db) {
	return new Promise(function (resolve, reject) {
		if (!(client.auth)) {
			resolve({ "result": false });
			return;
		}
		var q;


		q = `SELECT n.*
				FROM trade.news n ORDER BY n.n_date DESC;`;

		db.query(q, [client.t_id]).then(
			(value) => {

				resolve({ "data": value });

			});

		//console.log(obj, client);


	});
};